(12406, 445, '_faq_2_question', 'field_627232870e60e'),
(12407, 445, 'faq_2_answer', 'Congratulations on growing your business! When you’re ready to add more products or unit orders to your account, contact us at <a href=\"mailto:support@resourced.com\">support@resourced.com</a> to upgrade your your plan.'),
(12408, 445, '_faq_2_answer', 'field_627232940e60f'),
